```
//If the led is blinking too slowly or too fast, stop the led altogether.
var blinkRate = 50;

onBoardEvent(buttonL, "down", function(event) {
  blinkRate = blinkRate - 10;
  led.blink(blinkRate);
  if(blinkRate < 10){
  	led.off();
  }
});

onBoardEvent(buttonR, "down", function(event) {
  blinkRate = blinkRate + 10;
  led.blink(blinkRate);
  if(blinkRate > 200){
    led.off();
  }
});

```
