var colors = "white";

onBoardEvent(accelerometer, "data", function(event) {
  var pitch = accelerometer.getOrientation("pitch");
  var inclination = accelerometer.getOrientation("inclination");
  var roll = accelerometer.getOrientation("roll");
  
  //front to back
  if (pitch < -10) {
    colorLeds[0].on();
    colorLeds[9].on();
  }
  if (pitch > 10) {
    colorLeds[4].on();
    colorLeds[5].on();
  }
  
  //side-to-side
  if (roll > 20) {
    colorLeds[7].on();
  } 
  if(roll < -20){
    colorLeds[2].on();
  }
});
```
