// Keep the apple in the air by moving the paddle with the left and right arrows.
var paddle = createSprite(200, 375, 50, 10);
var apple = createSprite(200, 50);
apple.setAnimation("apple");
apple.velocityX=randomNumber(-1, -1);
apple.velocityY=randomNumber(5, 10);
apple.setCollider("circle",0,0,40);
function draw() {
  background("white");
  drawSprites();
  if (keyDown("right")) {
    paddle.x=paddle.x+5;
  }
  if (keyDown("left")) {
    paddle.x=paddle.x-5;
  }
  if (apple.x<0 || apple.x>400) {
    apple.velocityX=-1*apple.velocityX;
  }
  if (apple.y<0) {
    apple.velocityY=-1*apple.velocityY;
  }  
  apple.bounceOff(paddle);
}
```

