// Explore a background by linking the camera to the mouse.
var back = createSprite(200, 200);
back.setAnimation("stone_snow_1");
back.scale = 5;
camera.on();

function draw() {
  background("white");
  drawSprites();
  camera.x=World.mouseX;
  camera.y=World.mouseY;
}
