// Zoom in and out.
var back = createSprite(200, 200);
back.setAnimation("stone_snow_1");
camera.on();

function draw() {
  background("white");
  drawSprites();
  if (keyDown("up")) {
    camera.zoom = camera.zoom - 0.1;
  }
  if (keyDown("down")) {
    camera.zoom = camera.zoom + 0.1;
  } 
}
