// Create a flickering box by swapping sprite depths.
var sprite1 = createSprite(200, 200);
var sprite2 = createSprite(200, 200);
sprite2.shapeColor="black";
function draw() {
  background("white");
  drawSprites();
  var temp=sprite1.depth;
  sprite1.depth=sprite2.depth;
  sprite2.depth=temp;
}
