var target = createSprite(200, 200);
target.shapeColor="green";
var group = createGroup();
for (var i = 0; i < 5; i++) {
  group.add(createSprite(randomNumber(0, 400), randomNumber(0, 400), 20, 20));
}
function draw() {
  background("white");
  drawSprites();
  if (group.isTouching(target)) {
    target.shapeColor="red";
  }
}
