// Drive a square using the UP DOWN LEFT RIGHT keys.
var sprite = createSprite(200, 200);
function draw() {
  background("white");
  if (keyDown("up")) {
    sprite.y=sprite.y-1;
  }
  if (keyDown("down")) {
    sprite.y=sprite.y+1;
  }
  if (keyDown("left")) {
    sprite.x=sprite.x-1;
  }
  if (keyDown("right")) {
    sprite.x=sprite.x+1;
  }  
  drawSprites();
}
```
