// Keep the sprite up by moving the mouse over the sprite.
var sprite = createSprite(200, 200,25,25);
function draw() {
  background("white");
  if (mouseIsOver(sprite) && mouseDidMove()) {
    sprite.y=sprite.y-1;
  } else {
    sprite.y=sprite.y+1;
  }
  drawSprites();
}
