// Use 6 different images for a sprite, labeled "dieRed1" through "dieRed6", and randomNumber to roll a die.
var die = createSprite(200,200);
var count=0;
function draw() {
  background("white");
  count=count+1;
  if (count<20) {
    die.setAnimation("dieRed"+randomNumber(1,6));
  }
  drawSprites();
}
