var sprite = createSprite(200, 200);
sprite.setAnimation("target_red1_1");
function draw() {
  background("white");
  if (sprite.visible === true) {
    sprite.visible = false;
  } else {
    sprite.visible = true;
  }
  drawSprites();
}
