```
textSize(30);
var screen_text;
function draw() {
  background("white");
  var count = World.seconds;
  if(count > 2){
    screen_text = text("The game is starting!", 70, 200);
    if(count > 5){
      titleScreen();
    }
  }
  else{
     screen_text = text("Welcome!",130, 200);
  }
}
function titleScreen() {
  textFont("Monospace");
  stroke("green");
  strokeWeight(3);
  textSize(25);
  background("red");
  screen_text = text("Welcome to Map Blaster", 30, 200);
}
```
